import React from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useUser } from "../../context/userContext";
import { useEffect, useState } from "react";

function EditarItemCardapio() {
  const navigate = useNavigate();
    const location = useLocation();

  const apiRoot = process.env.REACT_APP_API_URL; // URL da API
  const { userType, userID, userName, setUserType, setUserID, setUserName } = useUser();
  const [showPerfilOptions, setShowPerfilOptions] = useState(false);

  const [ item ] = useState(location.state);

  const [inputNome, setInputNome] = useState("");
  const [inputDescricao, setInputDescricao] = useState("");
  const [inputPreco, setInputPreco] = useState("");

  const handleSairConta = () => {
    setUserType(null);
    setUserID(null);
    setUserName(null);
    navigate("/");
  };

  const handleEditarItemCardapio = async () => {
    if (!inputNome || !inputDescricao || !inputPreco) {
      alert("Preencha todos os campos");
      return;
    }

    try {
      const response = await fetch(`${apiRoot}/editarItemCardapio?pratoID=${item.PratoID}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          nome: inputNome,
          descricao: inputDescricao,
          preco: inputPreco,
        }),
      });

      const data = await response.json();

      if (data) {
        alert("Item atualizado com sucesso");
          navigate("/paginaRestaurante");
      } else {
        alert("Erro ao atualizar item");
      }
    } catch (error) {
      console.error("Erro ao atualizar item no cardapio", error);
      return;
    }
  };

  useEffect(() => {
    if (!userType || userType !== "Restaurante" || !userID || !userName || !item) {
      navigate("/paginaRestaurante");
    } else {
        setInputNome(item.Nome);
        setInputDescricao(item.Descricao);
        setInputPreco(item.Preco);
    }
  }, []);

  return (
    <div className="screen d-flex flex-column align-items-center justify-content-center no-select">
      <div className="background"></div>
      <header className="header d-flex flex-row justify-content-between p-3">
        <div className="d-flex align-items-center gap-4">
          <div className="header-title" onClick={() => navigate("/")}>Food-EUS</div>
          <div className="header-subtitle p-2" onClick={() => navigate(`/pagina${userType}`)}>
            Inicio
          </div>
        </div>

        <div className="d-flex align-items-center gap-4">
          <div className="profile-circle" onClick={() => setShowPerfilOptions(!showPerfilOptions)}></div>
          {showPerfilOptions && (
            <div className="profile-options d-flex flex-column p-3 m-2">
              <h4 className="profile-options-text align-self-center mb-3 bold">
                Olá, {userName}!
              </h4>
              <button className="red-button p-2 mb-2" onClick={() => navigate("/perfil")}>
                Perfil
              </button>
              <button className="red-button p-2 mb-2" onClick={() => navigate("/pedidosCliente")}>
                Pedidos
              </button>
              <button className="red-button p-2" onClick={() => handleSairConta()}>
                Sair
              </button>
            </div>
          )}
        </div>
      </header>

      <div className="card col-10 d-flex flex-column align-items-center justify-content-center mt-4 mb-4 p-4" style={{maxWidth: "43rem"}}>
        <h4 className="card-title my-4">Editar Item do Cardápio</h4>

        <div className="col-12">
          <div className="row d-flex flex-wrap">
            <div className="mb-2 d-flex flex-column col">
              <h6 className="card-text text-start px-2 mb-1">Nome</h6>
              <input type="text" className="card-input mb-2 p-2" value={inputNome} onChange={(e) => setInputNome(e.target.value)}/>
            </div>

            <div className="mb-2 d-flex flex-column col">
              <h6 className="card-text text-start px-2 mb-1">Preço</h6>
              <input type="text" className="card-input mb-2 p-2" value={inputPreco} onChange={(e) => setInputPreco(e.target.value)}/>
            </div>
          </div>
        </div>

        <div className="col-12">
          <div className="row d-flex flex-wrap">
            <div className="mb-2 d-flex flex-column col">
              <h6 className="card-text text-start px-2 mb-1">Descrição</h6>
              <input type="text" className="card-input mb-2 p-2" value={inputDescricao} onChange={(e) => setInputDescricao(e.target.value)}/>
            </div>
          </div>
        </div>
        <button className="red-button p-2 col-5" onClick={() => handleEditarItemCardapio()}>
          atualizar
        </button>
      </div>
    </div>
  );
}

export default EditarItemCardapio;
