import React from "react";
import { useNavigate } from "react-router-dom";
import { useUser } from "../../context/userContext";
import { useEffect, useState } from "react";

function Perfil() {
  const navigate = useNavigate();
  const apiRoot = process.env.REACT_APP_API_URL; // URL da API
  const { userType, userID, userName, setUserType, setUserID, setUserName } = useUser();
  const [showPerfilOptions, setShowPerfilOptions] = useState(false);

  const [inputNome, setInputNome] = useState("");
  const [inputEmail, setInputEmail] = useState("");
  const [inputCpf, setInputCpf] = useState("");
  const [inputTelefone, setInputTelefone] = useState("");
  const [inputEndereco, setInputEndereco] = useState({
    rua: "",
    numero: "",
    bairro: "",
    cep: "",
  });
  const [inputCulinaria, setInputCulinaria] = useState("");
  const [inputHorario, setInputHorario] = useState("");

  const handleSairConta = () => {
    setUserType(null);
    setUserID(null);
    setUserName(null);
    navigate("/");
  };

  const fetchUserInfo = async () => {
    try {
      const response = await fetch(`${apiRoot}/perfil?userID=${userID}&userType=${userType}`);

      if (!response.ok) {
        console.log(response.status);
        console.log(response.statusText);
        if (response.status === 500) {
          alert("Erro interno do servidor!");
        } else if (response.status === 404) {
          alert("Usuário não encontrado!");
        } else {
          alert("Erro ao carregar informações do perfil!");
        }
        return;
      }

      const data = await response.json();

      if (data) {
        if (userType === "Cliente") {
          setInputNome(data.Cliente.Nome);
          setInputEmail(data.Usuario.Login);
          setInputCpf(data.Cliente.CPF);
          setInputTelefone(data.Cliente.Telefone);
          setInputEndereco({
            rua: data.Endereco.Rua,
            numero: data.Endereco.Numero,
            bairro: data.Endereco.Bairro,
            cep: data.Endereco.CEP,
          });
        } else if (userType === "Restaurante") {
          setInputNome(data.Restaurante.Nome);
          setInputEmail(data.Usuario.Login);
          setInputCulinaria(data.Restaurante.Culinaria);
          setInputTelefone(data.Restaurante.Telefone);
          setInputHorario(data.Restaurante.Horario);
          setInputEndereco({
            rua: data.Endereco.Rua,
            numero: data.Endereco.Numero,
            bairro: data.Endereco.Bairro,
            cep: data.Endereco.CEP,
          });
        }
      } else {
        alert("Erro ao carregar informações do perfil");
      }
    } catch (error) {
      console.log(error);
      alert("Erro ao conectar com o servidor!");
    }
  };

  const handleAtualizarPerfil = async () => { 
    if (!inputNome || !inputEmail || !inputTelefone || !inputEndereco.rua || !inputEndereco.numero || !inputEndereco.bairro || !inputEndereco.cep) {
      alert("Preencha todos os campos!");
      return;
    }

    if (userType === 'Cliente' && !inputCpf) {
      alert("Preencha todos os campos!");
      return;
    } else if (userType === 'Restaurante' && (!inputCulinaria || !inputHorario)) {
      alert("Preencha todos os campos!");
      return;
    }

    const dataToSend = {
      nome: inputNome,
      email: inputEmail,
      userID: userID,
      userType: userType,
      telefone: inputTelefone,
      endereco: inputEndereco,
      ...(userType === 'Cliente' ? {
        cpf: inputCpf,
      } : {
        culinaria: inputCulinaria,
        horario: inputHorario,
      }),
    };

    try {
      const response = await fetch(`${apiRoot}/atualizarPerfil`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(dataToSend),
      });

      if (!response.ok) {
        console.log(response.status);
        console.log(response.statusText);
        if (response.status === 500) {
          alert("Erro interno do servidor!");
        } else if (response.status === 400) {
          alert("Erro ao atualizar usuário!");
        }
        return;
      }

      const data = await response.json();

      if (data === "emailExists") {
        alert("Email já cadastrado!");
        return;
      } else if (data === "cpfExists") {
        alert ("CPF já cadastrado!");
        return;
      } else if (data === "nomeExists") {
        alert("Nome de restaurante já cadastrado!");
        return;
      } else if (data === "updateOK") {
        alert("Update realizado com sucesso!");
      } else {
        alert("Erro ao atualizar perfil!");
      }
    } catch (error) {
      console.log(error);
      alert("Erro ao conectar com o servidor!");
    }
  };

  useEffect(() => {
    if ( !userType || (userType !== "Cliente" && userType !== "Restaurante") || !userID || !userName ) {
      navigate("/");
    }
    fetchUserInfo();
  }, []);

  return (
    <div className="screen d-flex flex-column align-items-center justify-content-center no-select">
      <div className="background"></div>
      <header className="header d-flex flex-row justify-content-between p-3">
        <div className="d-flex align-items-center gap-4">
          <div className="header-title" onClick={() => navigate("/")}>Food-EUS</div>
          <div className="header-subtitle p-2" onClick={() => navigate(`/pagina${userType}`)}>
            Inicio
          </div>
        </div>

        <div className="d-flex align-items-center gap-4">
          <div className="profile-circle" onClick={() => setShowPerfilOptions(!showPerfilOptions)}></div>
          {showPerfilOptions && (
            <div className="profile-options d-flex flex-column p-3 m-2">
              <h4 className="profile-options-text align-self-center mb-3 bold">
                Olá, {userName}!
              </h4>
              <button className="red-button p-2 mb-2" onClick={() => navigate("/perfil")}>
                Perfil
              </button>
              <button className="red-button p-2 mb-2" onClick={() => navigate(`/pedidos${userType}`)}>
                Pedidos
              </button>
              <button className="red-button p-2" onClick={() => handleSairConta()}>
                Sair
              </button>
            </div>
          )}
        </div>
      </header>

      <div className="card col-10 d-flex flex-column align-items-center justify-content-center mt-4 mb-4 p-4" style={{maxWidth: "43rem"}}>
        <h4 className="card-title my-4">Seu Perfil</h4>

        <div className="col-12">
          <div className="row d-flex flex-wrap">
            <div className="mb-2 d-flex flex-column col">
              <h6 className="card-text text-start px-2 mb-1">Nome</h6>
              <input type="text" className="card-input mb-2 p-2" value={inputNome} onChange={(e) => setInputNome(e.target.value)}/>
            </div>

            <div className="mb-2 d-flex flex-column col">
              <h6 className="card-text text-start px-2 mb-1">Email</h6>
              <input type="email" className="card-input mb-2 p-2" value={inputEmail} onChange={(e) => setInputEmail(e.target.value)}/>
            </div>
          </div>
        </div>

        {userType === 'Cliente' ? (
          <div className="col-12">
            <div className="row d-flex flex-wrap">
              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">CPF</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputCpf} onChange={(e) => setInputCpf(e.target.value)}/>
              </div>

              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">Telefone</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputTelefone} onChange={(e) => setInputTelefone(e.target.value)}/>
              </div>
            </div>

            <h6 className="card-subtitle my-3 col-12 text-center">Endereço</h6>

            <div className="row d-flex flex-wrap">
              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">Rua</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputEndereco.rua} onChange={(e) => setInputEndereco({ ...inputEndereco, rua: e.target.value })}/>
              </div>
              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">Número</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputEndereco.numero} onChange={(e) => setInputEndereco({ ...inputEndereco, numero: e.target.value })}/>
              </div>
            </div>

            <div className="row d-flex flex-wrap">
              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">Bairro</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputEndereco.bairro} onChange={(e) => setInputEndereco({ ...inputEndereco, bairro: e.target.value })}/>
              </div>
              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">CEP</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputEndereco.cep} onChange={(e) => setInputEndereco({ ...inputEndereco, cep: e.target.value })}/>
              </div>
            </div>
          </div>
        ) : (
          <div className="col-12">
            <div className="row d-flex flex-wrap">
              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">Culinária</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputCulinaria} onChange={(e) => setInputCulinaria(e.target.value)}/>
              </div>
              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">Telefone</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputTelefone} onChange={(e) => setInputTelefone(e.target.value)}/>
              </div>
            </div>

            <div className="row d-flex flex-wrapow">
              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">Horários de Funcionamento</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputHorario} onChange={(e) => setInputHorario(e.target.value)}/>
              </div>
            </div>

            <h6 className="card-text text-center px-2 mb-1 col-12">Endereço</h6>

            <div className="row d-flex flex-wrap">
              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">Rua</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputEndereco.rua} onChange={(e) => setInputEndereco({ ...inputEndereco, rua: e.target.value })}/>
              </div>
              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">Número</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputEndereco.numero} onChange={(e) => setInputEndereco({ ...inputEndereco, numero: e.target.value })}/>
              </div>
            </div>

            <div className="row d-flex flex-wrap">
              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">Bairro</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputEndereco.bairro} onChange={(e) => setInputEndereco({ ...inputEndereco, bairro: e.target.value })}/>
              </div>
              <div className="mb-2 d-flex flex-column col">
                <h6 className="card-text text-start px-2 mb-1">CEP</h6>
                <input type="text" className="card-input mb-2 p-2" value={inputEndereco.cep} onChange={(e) => setInputEndereco({ ...inputEndereco, cep: e.target.value })}/>
              </div>
            </div>
          </div>
        )}
        <button className="red-button my-3 p-2 col-5" onClick={() => handleAtualizarPerfil()}>Atualizar</button>
      </div>
    </div>
  );
}

export default Perfil;
